package com.crusoe.relaxedintegration.util.page;import java.util.List;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageImpl;import org.springframework.data.domain.Pageable;/** * PageFactory *  * @author  *  */public abstract class PageFactory {	/**	 * 创建Page对象	 * 	 * @param content	 *            记录集合	 * @param pageable	 *            分页相关参数	 * @param total	 *            总记录数	 * @return	 */	public static <T> Page<T> createPage(List<T> content, Pageable pageable, long total) {		return new PageImpl<T>(content, pageable, total);	}	/**	 * 创建Page对象	 * 	 * @param content	 *            记录集合	 * @return	 */	public static <T> Page<T> createPage(List<T> content) {		return new PageImpl<T>(content);	}}